import { useEffect, useState } from "react";

function CharacterCard({ character }) {
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    // Check if the character is already in favorites
    fetch('http://localhost:3000/favorites')
      .then(response => response.json())
      .then(favorites => {
        const alreadyFavorited = favorites.some(fav => fav.id === character.id);
        setIsFavorite(alreadyFavorited);
      })
      .catch(error => console.error('Error checking favorites:', error));
  }, [character.id]);

  const handleAddToFavorites = () => {
    if (!isFavorite) {
      fetch('http://localhost:3000/favorites', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(character),
      })
        .then((response) => {
          if (response.ok) {
            setIsFavorite(true);
            console.log('Character added to favorites:', character);
          } else {
            throw new Error('Failed to add character to favorites');
          }
        })
        .catch((error) => console.error('Error:', error));
    }
  };

  return (
    <article>
      <h3>{character.name}</h3>
      <img src={character.image} alt={character.name} />
      {!isFavorite && (
        <button onClick={handleAddToFavorites}>Add to Favorites</button>
      )}
      {isFavorite && <p>Already in Favorites</p>}
    </article>
  );
}

export default CharacterCard;
