import { useEffect, useState } from "react";

// Define default images for each house
const defaultHouseImages = {
  Gryffindor: "../data/red-wizard.png",
  Slytherin: "../data/green-wizard.png",
  Hufflepuff: "../data/blue-wizard.png",
  Ravenclaw: "../data/yellow-wizard.png",
  default: "https://example.com/default-house.jpg" 
};

function CharacterCard({ character }) {
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    // Check if the character is already in favorites
    fetch('http://localhost:3000/favorites')
      .then(response => response.json())
      .then(favorites => {
        const alreadyFavorited = favorites.some(fav => fav.id === character.id);
        setIsFavorite(alreadyFavorited);
      })
      .catch(error => console.error('Error checking favorites:', error));
  }, [character.id]);

  const handleAddToFavorites = () => {
    if (!isFavorite) {
      fetch('http://localhost:3000/favorites', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(character),
      })
        .then((response) => {
          if (response.ok) {
            setIsFavorite(true);
            console.log('Character successfully added to favorites:', character);
          } else {
            throw new Error('Failed to add character to favorites');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  // Determine which image to display
  const characterImage = character.image 
    ? character.image 
    : defaultHouseImages[character.house] || defaultHouseImages.default;

  return (
    <article>
      <h3>{character.name}</h3>
      <img src={characterImage} alt={character.name} />
      <p>House: {character.house || "Unknown"}</p>
      {!isFavorite ? (
        <button onClick={handleAddToFavorites}>Add to Favorites</button>
      ) : (
        <p>Already in Favorites</p>
      )}
    </article>
  );
}

export default CharacterCard;
